INSERT INTO public.numbers (detail_vip, pairtype, pairnumber, miracledetail, miracledesc, pairnumberid, pairpoint) VALUES ('ด้านดี พลังของราหู คนที่มีเลขนี้ตั้งแต่กำเนิด มักมีเสน่ห์ ดูดี โดดเด่น มีชื่อเสียง มั่งคั่งร่ำรวย 

ด้านเสีย แต่ถ้าไม่ระวัง และมีเยอะเกินไป อีกทั้งมีเลขร้ายอื่นๆมาร่วมด้วย จะมีคดีความ มีเรื่องมีราว ป่วย มีอุบัติเหตุ เพราะคนเลขนี้หยิ่ง,ลุ่มหลงมัวเมา หูเบาเชื่อใจคนง่าย ถูกยุยงได้ง่าย ,เจ็บไม่จำ มีอารมณ์ขัดแย้งในตัวเอง และมีอาการหยิ่งถือดี มีโทสะจริต ชอบการเอาชนะไม่ยอมสยบใครง่าย ๆ มักจะไม่ได้รับความจริงใจ จากเพศตรงข้ามและเพื่อนฝูง เป็นคนดื้อรั้น และเชื่อมั่นในตนเองสูง ชีวิตมักผิดพลาด ชีวิตมักมีมุมหักเหหรือมีเหตุให้ล้มเหลวเกิดขึ้นกับตนเองได้อย่างที่ไม่ทันตั้งตัว เพราะหากตั้งใจทำอะไร หรือจะช่วยเหลือใครแล้วละก็ใครจะห้ามปราม หรือให้เหตุผลทัดทานอย่างไรก็ไม่ฟัง ถ้าหลงใครแล้วมักจะหลงอย่างไม่ลืมหูลืมตา ทุ่มทุกอย่างแม้กระทั่งชีวิตของตัวเองเพื่อคนอื่น มักแบกรับภาระของคนอื่นไว้ ทำเพื่อนคนอื่นมากกว่าตัวเอง มักถูกนินทาหรือถูกใส่ร้ายอยู่เนืองๆ คนที่มีเลขนี้ตามมักมีโรคประจำตัวที่รักษาหายยาก โรคภัยไข้เจ็บเบียดเบียนอย่างเรื้อรัง มักเป็นโรคร้าย โรคกรรมโรคมะเร็ง ชีวิตเกิดอุบัติเหตุได้ง่าย ติดเหล้าติดยา บางครั้งติดการพนัน หรือหลงเชื่อการทรงเจ้า เข้าผี เชื่ออย่างผิดๆ คิดว่าตนเองเป็นผู้มีญาณหยั่งรู้ วิเศษกว่าคนอื่น คนเลข 12 ชีวิตสมรสไม่แน่นอนถ้ายังไม่มีแฟนใช้จะถึงขั้นไม่ได้แต่งงาน ถ้าแต่งงานแล้วตัวเลขจะทำให้ต้องแต่งงานไม่น้อยกว่า 1 ครั้ง คนเลข 12 มักมีคนไม่จริงใจหวังเงินหวังผลประโยชน์รายล้อมอยู่รอบด้าน บางครั้งรู้ว่าขาหลอกแต่เต็มใจให้หลอก ก็เพราะเป็นคนใจอ่อนนั่นเอง เลข 12,21, ถ้าตามใครแล้วรวยทุกคน มีเงินทุกคนแต่ชีวิตมักจะขึ้นๆลงๆและจบไม่สวยในตอนจบ
', 'R10', '12', 'เป็นคนที่มีอารมณ์ขัดแย้งกันในตัวเอง เป็นคนมี 2 บุคลิกคือ บางครั้งดื้อรั้น มุทะลุดุดัน บางครั้งก่อนโยน ว่าง่ายเชื่อฟัง เวลาที่อยากเอาชนะใครจะต้องเอาชนะให้ได้ไม่ยอมคน ชอบการเอาชนะไม่ยอมสยบใครง่ายๆ การที่ไม่ยอมและแข็งๆแบบนี้ก่อให้เกิดอุบัติเหตุต่อชีวิตได้ง่าย จะมีโรคภัยไข้เจ็บเรื้อรัง ทำการสิ่งใดมักจะมีอุปสรรค ความใจร้อนทำให้เกิดอุบัติเหตุเสมอ และเป็นการก่อศัตรูเพิ่มอีกด้วย ชอบใจอ่อนระวังจะโดนหลอกได้ง่าย เรื่องความรักทุ่มเทสุดตัว แต่ถ้าอกหักล่ะ ไม่เป็นอันทำอะไรเลย และมีโอกาสถูกคนปองร้ายได้', 'พลังแห่งสองบุคลิกในคนเดียว                                                                     ', 13, -50);
INSERT INTO public.numbers (detail_vip, pairtype, pairnumber, miracledetail, miracledesc, pairnumberid, pairpoint) VALUES ('ข้อเสีย คือ เป็นคนมั่นใจในตัวเองสูง เอาแต่ใจตัวเอง หยิ่ง ไม่ชอบอยู่ใต้บังคับบัญชาใคร กล้าได้กล้าเสีย ทำให้มีปัญหาในการทำงานและครอบครัว เลข 01 10 (รวมถึงเลขทุกคู่ที่บวกกันแล้วได้ 10 ) เป็นเลขของความพลัดพราก ครอบครัวไม่อบอุ่น ไม่เข้าใจกัน ไม่มีเวลาให้กัน มักต้องพลัดพรากจากกันพ่อไปทาง แม่ไปทาง ลูกไปทาง  ถ้าดวงแข็งก็จากเป็นแค่เลิกราหย่าร้าง ถ้าดวงอ่อนก็จากตาย ถ้าโชคดีดวงแข็งและอดทนได้อยู่ด้วยกันก็มักไม่ลงรอยกัน ไม่มีเวลาให้กัน อาจมีปากเสียงกันบ่อย ทะเลาะกันบ่อย ขัดแย้งกันไม่เข้าใจกัน มีมือที่สาม มีเรื่องพ่อแม่ญาติพี่น้องของอีกฝ่ายมารบกวน ชีวิตมักมีอุปสรรค และมักมีคดีความทุกคน มีคนปองร้าย บริวารมักให้โทษ เป็นคนอาภัพ ศัตรูรอบด้าน บริวารมักจะแข็งข้อ ก่อเหตุให้เดือดร้อนและกระทบกระเทือนใจมาให้ มักมีปัญหายุ่งยากภายในครอบครัวเสมอ ต้องมีภาระต้องรับผิดชอบ ต้องแบกภาระอันใหญ่หลวง

ชีวิตมักจะมีเรื่องระทมขมขื่นแอบแฝงอยู่ในใจ แม้มีเพื่อนมีบริวารมากแต่กลับเหงาและรู้สึกโดดเดี่ยว สุดท้ายจะมีโรคภัยไข้เจ็บเบียดเบียนอย่างเรื้อรัง โดยเฉพาะตา สมอง โรคความดันและหัวใจทุกรูปแบบ ใครที่ใช้เลขนี้จักเป็นคนที่คิดการสิ่งใด หรือทำสิ่งใดๆอยากทำให้ได้ดีที่สุดแต่บางครั้งก็ไม่สำเร็จ จนหักโค่นล้มลงได้ จึงทำให้ต้องแบกรับภาระหนี้สินมากมายในช่วงชีวิตหนึ่ง 

ข้อดี ชอบของแปลกใหม่ไม่ซ้ำใคร มักจะเป็นผู้นำในการคิดค้นสิ่งใหม่ๆ โดยหวังจะให้มีสิ่งนั้นเกิดขึ้นเพื่อเป็นประโยชน์ต่อผู้อื่น มีความทันสมัย ชอบการเดินทาง ชอบท่องเที่ยว บุคคลหมายเลขนี้บทจะรุ่งโรจน์ มีลาภ ยศ สรรเสริญ ก็จะได้มาอย่างมากมาย บทดวงตก ลาภยศก็สามารถสูญสิ้นได้ในพริบตา ชีวิตจะประสบความสำเร็จหรือล้มเหลวขึ้นอยู่กับการกระทำ และการตัดสินใจของตนเองเป็นหลัก คนเลขนี้มักตกต่ำลงในบั้นปลายระวังจะลำบาก', 'R10', '01', 'เป็นเลขแห่งผู้นำ ได้รับอิทธิพลจากดาวอาทิตย์ เป็นเลขแห่งความเชื่อมั่นในตัวเองสูง มีไหวพริบปฏิภาณดีเยี่ยม สติปัญญาว่องไวใจนักเลง กล้าหาญ มีความทะเยอทะยานที่จะเป็นใหญ่เป็นโต กล้าเสี่ยง ชอบออกหน้า ถือความคิดอ่านตนเป็นใหญ่ เป็นผู้นำคน ชอบเด่นชอบดัง เป็นที่เคารพนับถือคนทั่วไป ใจคอเด็ดเดี่ยว คิดการเช่นไรมักจะรีบทำเดี๋ยวนั้น มั่นคงมีการตัดสินใจได้รวดเร็ว ไม่โลเล ชีวิตมักจะมีการเดินทางไม่เกรงกลัวต่ออุปสรรคต่างๆ เป็นเลขของเจ้าชะตะตาที่มีความแข็งแรงมีพลังร้อนแรงไม่มีขีดจำกัดอยู่ ชีวิตประสบความสำเร็จดี มีชื่อเสียง มีสมบัติทรัพย์ มีความสุข ดีได้ด้วยปัญญาความสามารถของ ทำงานด้านรับราชการ ทหาร ตำรวจ เป็นเจ้าคนนายคน ดีนักแล', 'ดาวอาทิตย์ดาวพลังความเชื่อมั่น                                                                 ', 2, -10);
INSERT INTO public.numbers (detail_vip, pairtype, pairnumber, miracledetail, miracledesc, pairnumberid, pairpoint) VALUES ('ด้านดี เป็นดาวการเงิน เสริมส่งในเรื่องการงานการเงินให้ไหลคล่อง เงินหาง่ายหาเงินเก่ง มีเสน่ห์รุนแรง ดึงดูด ทำให้เป็นคนมีมิตรมาก มั่งมีศรีสุข สมบูรณ์พูนผล ชีวิตไม่ยุ่งยาก มีความรักที่ดี เป็นคนฉลาดมีปฏิภาณไหวพริบ ไม่ถือตัว เป็นคนดีมีเมตตา เอื้อเฟื้อช่วยเหลือผู้อื่น ผู้ใหญ๋และเพื่อนฝูงมักจะให้การอุปถัมภ์ค้ำชูเป็นอย่างดี กิริยามารยาทนุ่มนวลเป็นที่รักของคนทั่วไป เป็นคนมีสติปัญญาว่องไวแตกฉาน  เป็นคนมองการณ์ไกล เข้าใจสิ่งแวดล้อมรอบตัวได้อย่างรวดเร็วโดยไม่ต้องไต่ถาม เป็นคนละเอียดอ่อนไม่ฉาบฉวย เข้าถึงอารมณ์ศิลป์ โรแมนติก ใจดี ใจบุญ ใจเย็น ไม่ชอบการแสดงอำนาจ มักจะมีโชคลาภ เงินทองไหลเข้าหาได้ง่าย เงินทองไม่ขาดมือ และมีรายได้หลายทาง (เลขนี้เป็นเลขนึงที่ใช้ปรับคนใจร้อนใจร้ายปากร้ายถ้าเขาใช้ได้ถูกโฉลก)

หากใครเป็นศิลปิน และตกอยู่ภายใต้อิทธิพลเลข 24 มักจะเป็นศิลปินเอกมีชื่อเสียงโด่งดัง สามารถแสดงอารมณ์ความรู้สึกได้อย่างดีเยี่ยม (ตีบทแตก) เลข 24 ยังเหมาะกับพ่อค้าแม่ขาย เพราะมีความชำนาญงานปฏิภาณเฉียบแหลม เข้าใจงานอย่างลึกซึ้ง เข้ากับใครได้ง่าย พูดดี พูดเก่งพูดเพราะอ่อนหวาน บุคคลหมายเลข 24 จะทำให้ได้รับการช่วยเหลือจากผู้มีอำนาจอิทธิพล และบ่งบอกว่าจะต้องรู้จักเกี่ยวพันกับคนในสังคมชั้นสูงที่มีหน้าที่การงานใหญ่โต หมายเลขนี้ช่วยเพิ่มพูนความสำเร็จทางการเงินการงานได้เป็นอย่างดี และจะได้รับความสุขในด้านความรักด้วย

ด้านเสีย เป็นเลขดีมาก แต่อย่ามีเยอะมากเกินไป เพราะจะสุขนิยม เฉื่อย หลงตัวเอง ใช้เงินเก่ง ใจเย็นเกินไป ควรต้องปรับสมดุลด้วย 41,14,15,45,59,95,36,63

หมายเลข 24 ถ้าใครได้มาตั้งแต่กำเนิดเกิดมาเอง โดยไม่ได้เปลี่ยนแปลง มักเป็นผู้ที่เกิดมามีบุญวาสนา หรือกินบุญเก่านั้นเอง จะเป็นคนที่มีสติปัญญาดี เป็นนักพูดชั้นดี มีเพื่อนฝูงดีๆมาก มีบริวารคอยให้ความช่วยเหลือสนับสนุน เป็นคนที่มีเสน่ห์ดึงดูดใจ ให้คนอื่นรักชอบ เป็นคนน่ารัก มีโชคดีเกี่ยวกับความรักและเงินทอง เงินทองหามาได้โดยง่าย ชีวิตสุขสบายมาก จนบางครั้งถึงกับหลงตัวเองว่า ทำไมเกิดมาชีวิตนี้จึงดีไปเสียหมดทุกอย่าง แต่....ระวังสุดท้ายแล้วถ้าเขาเกิดมาดี แต่กลับไม่ทำความดีไม่สร้างบุญเพิ่มในชาตินี้ แถมไปรับเอาเลขร้ายๆเข้ามาในชีวิตโดยไม่รู้ตัว เมื่อบุญหมด บั้นปลายชีวิตจะตกต่ำลงในทุกๆเรื่องไม่ว่าจะเป็นทรัพย์สินเงินทองหรือความรักก็ตาม 
', 'D10', '24', 'เป็นเลขดีมาก เป็นดาวการเงิน เสริมส่งในเรื่องการงานการเงินให้ไหลคล่องเงินหาง่ายหาเงินเก่ง มีเสน่ห์รุนแรง ดึงดูด ทำให้เป็นคนมีมิตรมาก มีเพื่อนมาก และเพื่อนฝูงมักจะให้การอุปถัมภ์ค้ำชูและสนับสนุนเป็นอย่างดี เป็นค่าพลังที่สมบูรณ์มาก มีไหวพริบดี ฉลาด ไม่ถือตัว เข้าถึงง่าย ใครมาขอความช่วยเหลือไม่เคยปฏิเสธ แต่เป็นคนรอบคอบ รู้จักวางแผน ชีวิตประสบความสำเร็จดีมากเหมาะกับดารานักแสดง นักการเมือง โดยเฉพาะพ่อค้าแม่ค้า หรือ นักธุรกิจ เจ้าของกิจการควรใช้ เพราะจะเสริมด้านวาจา เสริมเสน่ห์ดึงคนรักคนอุปถัมถ์ ดึงเงินดึงลูกค้าได้ดี และที่สำคัญที่ควรรู้คือเลขนี้ยังช่วยปรับในคนที่มีอารมณ์ร้อนหงุดหงิดขี้โมโหให้เป็นคนเป็นคนมีกิริยามารยาท นุ่มนวล พูดดีพูดเพราะใจเย็นได้มากขึ้น และจะทำให้เป็นที่รักของคนทั่วไป แถมยังช่วยเสริมในด้านสติปัญญาให้มีความฉลาดว่องไวแตกฉาน เป็นคนหยั่งรู้การณ์ไกล เข้าใจสิ่งแวดล้อมรอบตัวได้อย่างเอาใจเขามาใส่ใจเราได้ดี งานที่ให้ผลดีจะเกี่ยวข้องกับศิลปะ การค้าขาย การเจรจาต่อรอง ต่างๆ แต่ถ้าทางศิลปะจะดีมากๆแต่โดยรวมส่งผลได้เกือบทุกอาชีพเลย ควรทำบุญเสริมบารมีเสมอเพื่อให้ความราบรื่นมีอย่างต่อเนื่องเพราะเลขนี้บางคนมักจะหลงและโลภเพราะอยากได้ก้จะได้และมักได้มาโดยง่ายถ้าไม่ระวังไม่เสริมดวงเขาจะทำโทษเอา', 'พลังแห่งความสำเร็จมหามงคล                                                                      ', 25, 80);
INSERT INTO public.numbers (detail_vip, pairtype, pairnumber, miracledetail, miracledesc, pairnumberid, pairpoint) VALUES ('ด้านเสีย ให้ระวังอย่าคบคนเลวคนพาลเพราะจะพาชีวิตตกต่ำ จะซวย จะมีคดีความ มีเรื่องมีราว ถูกทำร้ายได้ คนเลขนี้เมื่อได้ขึ้นหลังเสือแล้วชีวิตจะถอนตัวจากสิ่งเลวร้ายได้ยาก มักสนใจในสิ่งศักดิ์สิทธิ์จนลุ่มหลง จนถอนตัวไม่ขึ้น และชีวิตจะตกอยู่ในสภาพแวดล้อมที่ไม่ดี ถูกกลั่นแกล้ง ถูกกดดันได้ง่าย มีอารมณ์ฉุนเฉียว และขัดแย้งกับคนอื่นได้ง่าย ที่สำคัญระวังจะไปหลอกลวงผู้อื่น

ด้านดี ผู้ที่ได้หมายเลขนี้ เป็นเลขดีอีกเลขหนึ่ง ชีวิตจะสำเร็จได้อย่างโลดโผน ชีวิตจะประสบความสำเร็จได้ดี มีโชคลาภ มีเงินทองมีพวกพ้องมาก มักทำอะไรแปลกๆ มักแหวกแนว ความหมายคล้ายเลข 89', 'R10', '98', 'จะประสบความสำเร็จอย่างดีกับการทำงาน การค้า การลงทุนต่างๆ มีโอกาสได้ลาภลอย ชอบทำอะไรไม่ตามใครพิเศษน่าสนใจ มีคนคบค้าสมาคมมาก มีความสุขดี แต่อาจจะมีโอกาสหลงมัวเมาในบางเรื่องมากเกินไป ระวังการคบเพื่อนที่ไม่ดี', 'พลังลาภลอย                                                                                     ', 99, -35);
INSERT INTO public.numbers (detail_vip, pairtype, pairnumber, miracledetail, miracledesc, pairnumberid, pairpoint) VALUES ('ด้านดี คือมักจะส่งผลดีอย่างรวดเร็ว ร้ายก็รวดเร็ว (มีอิทธิพลจากกำลังดาวมหาอุจน์ 13) ทำให้มีโชค และมีอำนาจหน้าที่การงานสูง บ้างก็เป็นผู้บริหาร บ้างก็เป็นผู้จัดการ เป็นคนโดนเด่นในสายงานแต่………

ด้านเสีย แต่ในขณะเดียวกันก็จะตกต่ำล้มเหลวลงอย่างรวดเร็ว มีเหตุร้ายเกิดขึ้นอย่างไม่คาดฝันเช่นกัน เพราะเลข 1,3 เป็นดาวคู่ศัตรูกัน เมื่อมาอยู่ร่วมกัน  จะให้ทั้งคุณและโทษอย่างรวดเร็ว มักมีการเปลี่ยนแปลงอย่างรวดเร็ว  จึงทำให้เกิดทั้งเรื่องดี เรื่องร้ายได้อย่างฉับพลัน  ชีวิตที่ต้องวิ่งเต้นต่อสู้ หรือแข่งขันอยู่ตลอดเวลา เป็นคนที่รับผิดชอบต่อหน้าที่การงานดี แต่ชีวิตมักหักกลางคัน ซึ่งเกิดจากบุคคลที่ใกล้ชิดหรือผู้ที่ตนไว้วางใจ ชีวิตมักจะมีลูกน้องหรือมีผู้สนับสนุนเป็นอันดีในช่วงเริ่มต้นทำงาน แต่พอเริ่มจะดีก้าวหน้าขึ้น มักถูกหักหลังแทงข้างหลัง หมดความสำคัญหมดอำนาจในบั้นปลาย มีการเลื้อยขาก้าวอี้ หรือ ในกลุ่ใงานขาดความสามัคคีกัน จนเป็นเหตุให้สิ่งที่กำลังดำเนินอยู่ มีอันต้องล้มลง 

ผู้ที่ใช้หมายเลข 31 13 ต้องระวังเรื่องชู้สาว การหย่าร้าง ครอบครัวแตกแยก การทรยศหักหลัง อุบัติเหตุรุนแรง หากมีบริวารลูกน้อง ก็ต้องระวังเขาจะโกงเงิน จะแทงข้างหลัง มีคดีความ มีการผ่าตัดซ้ำ ชีวิตตอนต้นดีบั้นปลายร้าย ใครมีเลข 31 13 จงระวังให้หนักเพราะเป็นดาวอุบัติเหตุที่รวดเร็วและรุนแรง ต้องระวังอุบัติเหตุทุกรูปแบบ ทั้งภัยจากเครื่องจักร ความเร็ว ของแหลมคม เขี้ยวงา ผ่าตัด ตัดเชื้อ ช่องท้องรังไข่ รถคว่ำ ถูกอาวุธ ถูกคนลอบทำร้าย  ครอบครัวมีปัญหาแตกร้าว ชีวิตคู่ไม่ปกติสุข  ผู้ใหญ่ให้โทษ สายตามีปัญหา ผู้ใหญ่กดดัน  

เลข 13 31 จะได้มาซึ่งอำนาจ  แต่หากใช้อำนาจไปในทางที่ผิด มีความเห็นแก่ตัว  เอารัดเอาเปรียบไม่มีศีลธรรมแล้ว สุดท้ายจะมีอุปสรรคปัญหามาขวางกั้นชีวิตอยู่ตลอดเวลา หาความสมดุลให้กับชีวิตไม่ได้เลย ชีวิตขึ้นๆลงๆ หาความแน่นอนไม่ได้
', 'R10', '31', 'ชีวิตขึ้นอยู่กับความไม่แน่นอนของชีวิต มีอุปสรรคมากมายมาขวางกั้น กว่าจะถึงจุดหมายปลายทางสำเร็จ ไม่มีความมั่นคงในชีวิต ชีวิตมักจะโชคร้ายในเบื้องต้นและประสบความสำเร็จในบั้นปลาย มีความสูญเสียและผิดพลาดได้ง่าย สุขภาพไม่แข็งแรงป่วยบ่อย หรือมีอุบัติเหตุไม่คาดฝัน ความรักจะเกิดข้อผิดพลาด ข่มขื่น ความสุขเรื่องรักไม่มั่นคงอยู่เพียงชั่วคราว คนรักมักจะนอกใจเสมอ แต่ก็มีโอกาสประสบความสำเร็จในบั้นปลายแต่ก็หนักหนาเอาการกว่าจะผ่านไปได้ครับ', 'พลังแห่งความไม่แน่นอนของโชคชะตา                                                                ', 32, -85);
INSERT INTO public.numbers (detail_vip, pairtype, pairnumber, miracledetail, miracledesc, pairnumberid, pairpoint) VALUES ('ด้านดี เป็นผู้ที่มีความกล้าหาญ มีความขยันขันแข็ง หัวคิดทันสมัย เป็นคนเก่ง เลขนี้สามารถส่งผลให้ชีวิตรุ่งโรจน์ได้อย่างผาดโผน ประสบความสำเร็จมีทุกสิ่งทุกอย่างทั้งเงินงานความรัก แต่…..

ด้านเสีย ขณะเดียวกันความรุ่งโรจน์ก็อาจล้มครืนลงมาได้อย่างฉับพลัน เหมือนวิมานทลาย  ชีวิตหาความสมดุลได้ยากเพราะชีวิตมักจะเปลี่ยนแปลงอย่างรวดเร็ว เช่น ธุรกิจการค้ากำลังเจริญรุ่งเรือง แต่ก็จะเกิดมีอุปสรรคมาปิดกั้นอย่างปัจจุบันทันด่วน หรือธุรกิจการงานกำลังประสบความสำเร็จแต่เจ้าของถูกจับหรือก็มีอันเป็นไป ได้แต่งงานได้เลื่อนตำแหน่งแต่ป่วยเจ็บประสบอุบัติเหตุ เป็นต้น จึงต้องระวังไม่ควรที่จะประมาท เพราะดาวคู่นี้เป็นดาวคู่ร้อนคู่อุบัติเหตุเมื่อมาอยู่ร่วมกัน จึงต้องระมัดระวังเรื่องอุบัติเหตุหรือเรื่อร้ายๆกะทันหันและมีเหตุร้ายแรงอย่างฉับพลัน เช่น การป่วย การบาดเจ็บ การผ่าตัดบ่อยๆ ให้ระวังเรื่องอุบัติเหตุและการผ่าตัดให้มาก ระวังรถคว่ำ ภัยจากอาวุธ เขี้ยวขอ ถูกคนลอบทำร้าย จนทำให้สูญเสียชีวิตได้ ให้ระวังการทะเลาะวิวาท ถูกจับ มีคดี การเจ็บป่วยจากโรคประสาท โรคผิวหนัง และระวังการถูกแย่งชิงตำแหน่งหน้าที่การงานความรักด้วย และคนเลขนี้มักมีเรื่องอื้อฉาวเกี่ยวกับการครองชีวิตคู่ การทะเลาะวิวาทในครอบครัว ชีวิตสมรสไม่แน่นอน ไม่ได้แต่งงาน หรือแต่งไม่น้อยกว่าหนึ่งครั้ง 

ชีวิตมักจะหักกลาง มักมีปัญหาและอุปสรรคมากมาย มักล้มเหลวในหน้าที่การงาน มักถูกทรยศหักหลัง ถูกคดโกงไม่ซื่อสัตย์ กลายเป็นคนอมทุกข์ มักมีเรื่องสะเทือนใจอย่างรุนแรงในชีวิต ถูกเพื่อนฝูงญาติมิตร เกลียดชัง ดูถูกเหยียดหยาม จิตใจมักได้รับความเจ็บซ้ำหรือ เจ็บปวดอย่างรุนแรงอยู่เสมอๆ ใครที่ใช้เลขนี้ชีวิตมักจะพบกับจุดแตกหักได้อย่างไม่คาดฝัน มีความไม่แน่นอนในชีวิตสูง บางครั้งจะได้อยู่แล้วกลับไม่ได้ ชีวิตต้นร้ายปลายดี แต่กว่าจะผ่านวัยต้นของชีวิตไปได้ อาจจะไม่มีชีวิตอยู่ก็ได้ ', 'R10', '03', 'มักเป็นคนมีจินตนาการสูง จิตใจอ่อนไหว มองโลกในแง่ดี บางครั้งคิดมาก คิดเล็กคิดน้อย ขี้น้อยใจ ช่างคิด ช่างฝัน ชอบศิลปะ ชอบของละเอียดอ่อน มีอารมณ์ศิลปิน ไวต่อความรู้สึกสูง เปลี่ยนแปลงอารมณ์ง่าย ด้วยเหตุนี้จึงเชื่อใจคนอื่นได้ง่าย ชอบชีวิตอิสระ ใครอยู่ใกล้แล้วจะมีความสุข เป็นคนอ่อนไหวแต่ถึงอย่างไรก็ตามเมื่อมีเหตุการณ์ไม่คาดคิด จะควบคุมสติได้ดี มีความกล้าหาญ เข็มแข็ง หลิกผันสถานการณ์ได้ดี ทำงานเป็นอาร์ตทิส ศิลปิน ดีไซด์เนอร์ นักออกแบบ สถาปนิก ครีเอทีฟ อาร์ตไดเร็คเตอร์ ฝ่ายศิลป์ทั้งหลาย นักออกแบบ ผู้ที่ต้องใช้ศาสตร์และศิลปะมาประกอบอาชีพจะรุ่งเรือง ติดที่เป็นคนดื้อรั้นแบบดื้อเงียบ ช่างฝันและขี้อาย เจ้าระเบียบ ขี้จุกจิก ต้องลดความดื้อและความอายลงบ้างจะดี', 'ดาวอังคารดาวพลังแห่งความกล้าหาญชาญชัย                                                          ', 4, -50);
INSERT INTO public.numbers (detail_vip, pairtype, pairnumber, miracledetail, miracledesc, pairnumberid, pairpoint) VALUES ('เป็นสาเหตุให้เกิดการเปลี่ยนแปลงกะทันหัน มักมีเหตุการณ์เกิดขึ้น แบบไม่คาดฝันโดยไม่รู้ตัว 

