func getColor(assignedColors string, index int) string {
	parts := strings.Split(assignedColors, ",")
	if index < len(parts) && parts[index] != "" {
		return parts[index]
	}
	return "#E5E7EB"
}

func getFirstChar(s string) string {
	runes := []rune(s)
	if len(runes) > 0 {
		return strings.ToUpper(string(runes[0]))
	}
	return ""
}

